INSERT INTO blog_posts (id, title, content, excerpt, image_url, author, created_at, updated_at, published) VALUES
(5, 'הארכיטקטורה של השינה', 'אמצע הלילה- נשמעת אזעקה. אני מבינה שיש אזעקה אבל לא ממש מבינה איפה אני, איפה המתג של האור, ומה עלי לעשות. אתם וודאי מכירים את תחושת הבלבול כשמקיצים משינה עמוקה. האם היקיצה שלנו יכולה להראות אחרת? התשובה היא - כן. והיא תלויה בשלב השינה בו אנחנו נמצאות.כשאנו ישנות מערכות הגוף עובדות! הן מתקנות נזקים, מחדשות רקמות, נלחמות בזיהומיםומיטיבות את תפקוד המוח. אבל כל זה אינו מתרחש בבת אחת הגוף שלנו עושה עבודה חשובהזו בשלבים. לשם כך השינה מחולקת למחזורי שינה האורכים כתשעים דקות, ובכל אחדממחזורי השינה מופיעים (כמעט) כל השלבים. מחזורי השינה חוזרים על עצמם כחמש פעמיםבלילה, וביניהם יתרחשו יקיצות שאת רובן לא נזכור (היקיצה משרתת מטרות פיזיולוגיותוהישרדותיות, כהגנה מפני סכנות).אך לפני שאכנס לשלבי השינה אסביר את שני סוגי השינה: שינה ללא תנועת עיניים המכונהNREM (Non Rapid Eye Movement) , ושינה עם תנועת עיניים המכונה REM (Rapid Eye Movement), המכונה גם "שנתחלום". שינה מסוג  NREM מורכבת משלושה שלבים, ושנת הREM היא שלב בפני עצמו.', '', '/assets/uploads/blog-1762616028545-420732630.jpg', 'מאיה קורן שכטמן', '2025-10-04 09:23:57', '2025-11-08 15:35:11', true),
(6, 'למה קפאין מדיר שינה מעינינו?', E'מהרגע שאנחנו  מתעוררים מתחיל להצטבר במוח חומר כימי בשם אדנוזין. הוא מעודד שינה ויוצר את מה שנקרא "לחץ שינה" (sleep pressure). כשהאדנוזין נקשר לקולטנים הייעודיים שלו אנחנו מתחילים להרגיש עייפות.\nהעייפות הזו נעלמת רק כשהאדנוזין מתנקה בזמן השינה, או כשחומר אחר- קפאין, דוחק אותו הצידה. הקפאין דומה במבנהו לאדנוזין, ולכן הוא "מתחפש" אליו ונקשר לאותם קולטנים, וכך חוסם את אותות העייפות. התוצאה: נרגיש ערנות, גם אם גופנו כבר זקוק למנוחה.', 'קפאין- אנחנו אוהבים אותו, מסתמכים עליו, ולא פעם מרגישים שאנחנו לא מתפקדים בלעדיו. מהו המנגנון של הקופאין שגורם לנו לרצות ממנו עוד', '/assets/uploads/blog-1762616964918-157524773.jpg', 'מאיה קורן שכטמן', '2025-10-04 09:23:57', '2025-11-08 15:52:48', true),
(7, 'בין אלכוהול לשינה', 'אמנם אלכוהולמשרה עלינו כבדות נעימה, עד עייפות. הוא עושה זאת בהיותו חומר סדטיבי (sedative) - כזה שמדכא את פעילות מערכתהעצבים המרכזית. הוא נקשר לקולטנים במוח שמונעים מתאי העצב "לירות" את הדחפיםהחשמליים, ולכן הכל מרגיש רגוע יותר ומשוחרר, ואפילו כבד ורפוי.עד כאן- סטלה נחמדת. אבל האלכוהול מערער את השינה בשתי צורות:1.אלכוהול מפצל את השינה: הוא גורםלהתעוררויות קצרות ובלתי מורגשות במהלך הלילה וקוטע את רצף השינה, ובהתאם גם פוגע באיכותהמשקמת של השינה. לרוע המזל, את רוב ההתעוררויות הללו לא נזכור ולכן נתקשה לקשר ביןצריכת אלכוהול לתחושת עייפות ביום המחרת. 2.אלכוהול מדכא את שנת ה R.E.M. ("שנת החלום")כשהגוףמעבד את האלכוהול הוא מייצר חומרים שחוסמים את יכולת המוח לחולל שנת R.E.M. למעשה הוא אחד ממדכאי שנתהREMהחזקים ביותר שמוכרים.', 'אם עד היום נעזרת באלכוהול "להוריד הילוך" ולהירגע לפני השינה- הפוסט הזה הוא עבורך.', '', 'מאיה קורן שכטמן', '2025-10-04 09:23:57', '2025-11-08 16:12:04', true),
(10, 'ציפורי יום או ינשופי לילה?', 'מה זה כרונוטיפ?הכרונוטיפ הוא הנטייה הטבעית למקצבי הערות והשינה שלנו. הוא מושפע מהגנטיקה, וכמעט בלתי תלוי בבחירה. אפשר לחלק את האוכלוסייה לשלוש קבוצות עיקריות:אנשי הבוקר (כשליש מהאוכלוסייה)עופות לילה (גם שליש)ובתווך, אלו שהם לא בוקר ולא לילה. כמו כל דבר בטבע- גם לזה יש סיבהבמהלך האבולוציה האדם התפתח כך שינום את שנתו עם כל בני השבט. תכונה שעלולה להמית כיליון על חברות שלמות, אילולא יצר הטבע הבדלים בין הפרטים. לכן בעוד שציפורי הבוקר פרשו מוקדם למיטה, עופות הלילה שמרו על המחנה, ועם זריחת השמש- התחלפה המשמרת.', 'כציפור בוקר מובהקת, תמיד היה לי קשה להבין. אבל היום אני יודעת: זה לא עניין של עצלות או העדפה – זה פשוט הכרונוטיפ שלכם.', '/assets/uploads/blog-1762618795584-551997460.jpg', 'מאיה קורן שכטמן', '2025-11-08 16:21:20', '2025-11-08 16:21:20', true),
(11, 'שנ"צ- כדאי לי או לא?', 'ראשית, אני מבקשת סליחה מראש ממי שעבורו שנ"צ הוא רק בגדר פנטזיה... ומאחלת לה להתגשם.כבני אדם, עוצבנו לישון בין 7 ל־9 שעות בלילה – אבל האם זה מספיק? האם ייתכן שאנחנו זקוקים גם לפרק שינה נוסף במהלך היום?רובנו נוטים לייחס את נפילת האנרגיה של שעות הצהריים לארוחה שזה עתה סיימנו. אבל – אולי תופתעו לגלות שעייפות זו ניתנת לזיהוי בגלי המוח שלנו גם אילו לא אכלנו דבר. כלומר, יתכן שאנחנו יצורים שמעוצבים לשינה בי־פאזית, הכוללת שתי פעימות שינה ביממה – לילה, ו... כן, גם צהרים.', '', '', 'מאיה קורן שכטמן', '2025-11-09 10:52:45', '2025-11-09 10:52:45', true),
(12, 'על שינה ברפואה הסינית', 'בקרוב', '', '/assets/uploads/blog-1768560596760-952327225.webp', 'מאיה קורן שכטמן', '2026-01-16 11:23:18', '2026-01-16 11:23:18', true);
INSERT INTO pages (id, page_key, title, content, image_url, updated_at) VALUES
(1, 'home', 'בית', 'שוב השינה לא מגיעההמוח מוצף מחשבות תירגעי. תירגעי. תירגעי!אני יודעת, אחר כך ארדם ואתעורר וארדם ואתעוררואתעוררלכל אחד ואחת מאתנו התמודדות שונה עם שעות הלילה. גם הביטויים של הפרעות השינה שונים ומלווים ברגשות מגוונים. אני רואה אתכם כאשר אתם מגיעים אלי לטיפול- עייפים, מתוסכלים, מיואשים, סובלים מבעיות בריאות שנולדות כתוצאה מחוסר בשינה. ובכל זאת- אני שמחה שאתם מגיעים. זה אומר לי שעדיין יש בכם רצון וכוח לשנות את המצב. מתוך ההתמודדות האישית שלי עם היעדר שינה, וההבנה עד כמה היא חשובה- בחרתי להתמקד בתחום זה ולעזור לשנות את חוויית הלילות שלכם.  על הטיפולהטיפול מבוסס על רפואה סינית. הוא נפתח באבחנה באמצעות מגע, התבוננות והקשבה. האבחנה מכוונת אותי באלו נקודות וערוצים (מרידיאנים) בגוף עליי לטפל כדי לתמוך ולעזור לגוף שלך לחזור לאיזון.  חלק אחד של הטיפול מתקיים אצלי בקליניקה: באמצעות שיאצו, דיקור וכלים נוספים מהרפואה הסינית.חלקו השני ממשיך אצלך בבית: באמצעות נטילת פורמולת צמחי מרפא ובאמצעות התאמת הרגלי חיים שיטיבו איתך ועם השינה שלך. אני מתאימה את הטיפול, על כל חלקיו, אליך אישית. ונמצאת עבורך לליווי, הקשבה ותמיכה, לאורך כל הדרך. (עוד על הטיפול) כמה מילים עליהגעתי לתחום הטיפול לפני כ 27 שנה. בתחילת הדרך כעובדת סוציאלית עם מתמודדי נפש, ובהמשך, שנים רבות כמטפלת במגע. לאורך השנים ראיתי שאנשים רבים מגיעים לטיפול עם כאבים פיזיים, שמאחוריהם בעיות שינה כרוניות שאינן מקבלות את תשומת הלב. ראיתי שכאשר הטיפול התמקד בשיפור השינה, ההשפעה על הכאבים הייתה מדהימה. מלחמת אוקטובר 23'''' הציפה את בעיות השינה לתודעה הציבורית והאישית. באותה תקופה טיפלתי במטה משפחות החטופים, שם גבר הצורך לתת מענה סביב נושא השינה. תקופה זו סייעה לי למקד את עולם הטיפול שלי לתחום זה.(הפניה לעמוד אודות)--לקוחות מספריםלשים חוות דעת מהאתר הישן, מביז, מגוגל? חוות דעת חלונות זזים? —---בלוגבתרבויות קדומות השינה נתפסה כ"אח קטן" של מוות: מצד אחד ידעו כל כך מעט על שינה. מצד שני ידעו את הדבר החשוב ביותר - שהלילה מאפשר לנו כל יום התחלה חדשה. זו נקודה שכדאי להקדיש לה מחשבה עמוקה ולהבין את המשמעות שלה עד הסוף, ולכן אניח בבלוג מידע מדעי ומחקרים על השינה, לצד ידע מעשי וכלים פרקטיים לשיפור השינה, מחשבות וחוויות מהקליניקה. קריאה מהנה. (לשים קישור לבלוג) יצירת קשר', '/assets/uploads/blog-1768559676802-141760348.jpg', '2026-01-16 10:40:45'),
(2, 'about', 'אודות', '"כשהילד ישן, את ישנה!" צלצל באוזניי המשפט שמוכר לכל אם צעירה. זרקתי מבט במחוגי השעון והתמלאתי ייאוש. עוד מעט מגיע הבוקר ואני לא מצליחה להירדם!!!  מעל שמונה-עשרה שנה עברו מאז אותם רגעים, שבאופן רשמי הכניסו אותי לעולם האימהות ולעולם האינסומניה.שמי מאיה קורן שכטמן. אני מטפלת ברפואה סינית: דיקור, צמחי מרפא ושיאצו.הלב שלי תמיד נמשך לליווי אנשים שזקוקים לעזרה, ואני נמצאת בעולם הטיפול כבר מעל 25 שנה. בהתחלה כעובדת סוציאלית עם מתמודדי נפש ובהמשך כמטפלת ברפואה סינית.באמצעות טיפול ללא מילים, טיפול שמתכנס כל כולו לרגע המזוקק של הנחת יד או מחט- מצאתי בתוכי את השקט, ואת היכולת להעניק את השקט לאחרים.  כמו רבים, גם לי מלחמת ה7 באוקטובר הייתה אירוע מכונן שהשפיע עליי באופן אישי ומקצועי: במשך כמעט כשנתיים טיפלתי במשפחות החטופים. מדי שבוע הבטתי בעיניים ונגעתי בגוף, בפחד ובכאב. בטיפולים אלו, ובטיפולים אצלי בקליניקה- בלט במיוחד הקושי בשעות הלילה: השינה הייתה אחת הראשונות להיפגע אצל אנשים רבים. הייתה הראשונה להיפגע גם אצלי. התחלתי להעמיק בתחום הפרעות השינה ברפואה הסינית, העמקתי במחקר העכשווי ובכלים התנהגותיים (כ-CBTi). בחרתי להתמקד ולתת מענה לנשים וגברים ששנתם פגועה. הקליניקההקליניקה, בה אני זוכה לטפל מעל 14 שנה, נמצאת במקום קסום וירוק ברמת אביב. היא חלק מקומפלקס טיפולי שכולל גם סטודיו ליוגה וחדר לפסיכולוגית. בפנים יש אנרגיה נשית חזקה וטובה, בחוץ- הרבה ירוק ושקט. לחדר הטיפולים שלי יש כניסה ישירה. לצערי, היא איננה מונגשת לכיסאות גלגלים. הלקוחות שליהאנשים שמגיעים אלי לטיפול נמצאים בשלבי חיים שונים, ומתמודדים עם הפרעות בשינה:  קושי להירדם, יקיצות מרובות, הזעות לילה, חלימה סוערת, עייפות כרונית, ועוד. אנשים שזקוקים בבוקר למספר כוסות קפה כדי לסחוב את היום, שמתמודדים כל יום מחדש עם העצבנות, חוסר הריכוז, התשישות, ההשמנה והכאבים הגופניים- שמלווים חוסר בשעות שינה. למרות שחוויתי הרבה לילות לבנים, אני מאוד נזהרת מלומר שאני מבינה את החוויה שלכם. אני רק יכולה לספר לכם על תחושות שאני חוויתי: תסכול, ייאוש, חשש להיכנס למיטה כשעוד לילה לבן מחכה לי בפינה. מכיוון שהחוויה היא כל כך אישית, חשוב לי מאוד לשמוע אותך: איזה תחושות, רגשות ומחשבות מלווים אותך סביב נושא השינה. ואני מבטיחה לך שאעשה ככל שביכולתי להיות לצידך ולתמוך בך בתהליך לשיפור הלילות שלך. הדרך המקצועית שליבגיל 24 מצאתי את עצמי יושבת מול גרפולוגית ביעוץ מקצוע: מה הכי מתאים לי ללמוד, שאלתי אותה. היא הביטה בדף מלא בכתב ידי, ובלי להסס אמרה: את צריכה ללמוד עבודה סוציאלית! אבל… אבל מה עם רפואה סינית? שאלתי. את עוד תגיעי לזה, ענתה.ואכן את עולם הטיפול פגשתי כעובדת סוציאלית בשיקום מתמודדי נפש. למדתי אותם וליוויתי בבניית תוכניות שיקום והדרכת אנשי הצוות. למרות אהבתי למקצוע הרגשתי שהמגע הפיזי חסר לי מאוד. לכן במקביל עשיתי קורס מסאג'''' רפואי במשך שנה. ועדיין הרגשתי שחסר לי עומק בטיפול. כשהגעתי ללמוד שיאצו לפני 21 שנה- התאהבתי. הרגשתי שהגעתי הביתה. עזבתי את ההוסטלים ומתמודדי הנפש, והתחלתי לטפל בקופת-חולים מכבי. את הטיפול שלי במגע מובילה אינטואיציה טובה וחזקה, אך הסקרנות והצורך להעמיק ולהבין יותר- דחפו אותי להגשים לבסוף את החלום של גיל 24, וללמוד רפואה סינית. לימודים שאמנם נתנו לי כלים חדשים לטיפול, אך בעיקר נתנו לי בסיס של הבנה עמוקה שהיה חסר לי כמטפלת בשיאצו. ומאז אני ממשיכה ללמוד ולהעמיק בעולם הרפואה הסינית ללא הרף. עולם שהוא מופלא בעיני, גאוני בפשטותו, ואינסופי. הסמכות•	עבודה סוציאלית BSW - אוניברסיטת בן גוריון, עם התמחות בשיקום מתמודדי-נפש בבית החולים הפסיכיאטרי בבאר שבע. •	הדרכה (supervision) בשיקום בבריאות הנפש- קורס ללימודי המשך מטעם מכון מגיד, להנחיית עובדים פרה-רפואיים בתחום השיקום. •	לימודי עיסוי רפואי- קורס שנתי מטעם מכללת תלתן.•	שיאצו- לימודים של 3 שנים מטעם מכללת תמורות.•	השתלמות אינטגרטיבית בשיאצו באונקולוגיה ובהמטורונקולוגיה- קורס שנתי במרכז דוידוף, בית חולים בילינסון.•	רפואה סינית- דיקור וצמחי מרפא. לימודים של 4 שנים במכללת "ברושים" •	אבחנות מתקדמות ברפואה סינית- לימודי המשך להעמקת דיוק האבחנה והטיפול. קורס שנתי. ', '/assets/uploads/blog-1768564400209-765559073.jpg', '2026-01-16 11:53:51'),
(3, 'treatment', 'על הטיפול', 'אני מאמינה בכוחותיו של הגוף לשקם את עצמו. התפקיד שלי ושלך הוא לעזור לו למצוא את הכוחות הללו. אני עושה זאת בכמה דרכים: במגע (שיאצו), בדיקור ובהתאמת צמחי מרפא סיניים. מנחה אותי קשב עמוק, הבנה של הקשר בין הגוף והנפש, וידע שרכשתי בהרבה שנות טיפול- כדי לתת מענה מדויק ואישי. תהליך הטיפולהפגישה הראשונה שלנו מתחילה בשיחה טלפונית קצרה, וממשיכה בפגישתנו פנים מול פנים. בקליניקה אנחנו לומדים אחת את השני.ה. אני עושה אבחון שמבוסס על מגע, התבוננות והקשבה, ומגבשת אסטרטגיה טיפולית. כבר בפגישה הראשונה נעשה טיפול קצר, ולאחריה אשלח לך סיכום מפורט, שמסביר את הכיוון הטיפולי. חשוב לי שהדרך בה אנחנו צועדים תהיה ברורה, והציפייה שלך למענה טיפולי תהיה מותאמת, לכן למרות שלפעמים זה קצת סינית- אנסה לפשט את הדברים וכמובן לענות על כל השאלות. כל טיפול אפתח בדרך כלל במגע קצר. אני מוצאת שיש לכך ערך עצום לבניית שקט פנימי ופיזי, ליצירת אמון, ולדיוק האבחנה. הדיקור הוא מעין שפה שהגוף והמוח שלך יודעים להבין. הדיקור שמגיע אחרי מגע הוא מדויק יותר, ופוגש אותך במקום של מוכנות לקבל ולפעול. הריפוי הוא תהליך שמקבל הכוונה בקליניקה וממשיך אצלך בבית, בשתי דרכים:1.	נטילת צמחי מרפא. לצמחים חלק משמעותי בטיפול. הם מגיעים מיובשים ומעורבבים יחד על פי מרשם שאני נותנת, כלומר- הם מותאמים עבורך באופן אישי ומשתנים בהתאם להתקדמות התהליך הטיפולי. 2.	התאמת הרגלי חיים מיטיבים- להרגלי היום שלנו יש השפעה קריטית על הלילה, ועל הבריאות הכללית שלנו. משום כך התאמת הרגלי חיים פשוטים, שמותאמים לגוף ולשלב החיים שלך, הם חלק בלתי נפרד מהטיפול. כמו שהטיפול הוא תהליך, גם שינוי הרגלים אינו אמור לקרות בבת אחת. זהו תהליך שדורש סבלנות, ואני נותנת מקום לקשיים שמלווים אותו. תדירות הטיפוללתדירות המפגשים ישנה משמעות טיפולית. כדי שניצור שינוי ונטמיע אותו, חשוב לעשות בהתחלה טיפול שבועי. כמות ותדירות הטיפולים תשתנה בהתאם לאופי הבעיה והזמן שהיא מלווה אותך. השאיפה שלי היא לסייע לך לעשות את השינוי המהיר ביותר כך שתדירות הטיפולים תפחת בהדרגה, עד שהתמיכה תישאר על פורמולה בלבד, ובהמשך נשחרר גם אותה. ובסופו של יום, הטיפול נועד להחזיר לך שינה יותר איכותית ורציפה, שתשפיע לטובה על הימים שלמחרת, על הבריאות הכללית והחיוניות שלך. ', '/assets/uploads/blog-1768560123265-62573239.jpg', '2026-01-16 10:43:35'),
(4, 'sleep-medicine', 'כשגוף ונפש חוזרים למקצב הטבעי- על שינה ברפואה הסינית', E'השינה היא אחת מהפונקציות הבסיסיות והעמוקות ביותר בגוף. ברפואה הסינית היא נחשבת לזמן שבו הנפש (shen), חוזרת אל מקומה בלב, והגוף משקם את האנרגיות החיוניות שלו: דם, צ''''י וyin. היא נתפסת כחלק בלתי נפרד מהמקצב הטבעי של היקום, ומשקפת את האיזון שבין יין ויאנג.yin & yang תנועה בין יום ללילההיין והיאנג הם שני כוחות מנוגדים זה לזה ומשלימים זה את זה. הם מהווים מושגי יסוד בעולםהרפואה הסינית, ובסיס להתבוננות על כל התופעות. לענייננו, היום הוא זמן של יאנג- פעילות, חום ותנועה.הלילה הוא זמן של יין- שקט, קרירות, רוגע והתכנסות. כשהערב יורד- היאנג שלנו "שוקע" ונח בתוך היין. אך כאשר אנחנו לא מאוזנים היאנג מתקשה להתכנס, הנפש מתקשה להיות "מעוגנת" ושקטה, והשינה מופרעת.מה גורם לגוף ולנפש לצאת מאיזון? כמטפלת ברפואה סינית איני מסתכלת על הפרעות שינה כסימפטום בודד, אלא רואה בהן ביטוי לחוסר הרמוניה פנימית. לכל אדם יש סיפור שונה, ולכן גם השורש לחוסר השינה שונה. אסבירכמה מהדפוסים שעשויים לבטא יציאה מאיזון וכיצד זה יתבטא בשינה: סטגנציה של Qi הכבד: מחשבות מתרוצצות, מתח, קושי "לכבות את הראש". שינה שטחית, חלימה מטרידה, יקיצה סביב השעה 3:00. חום בלב:  יקיצות מלוות בהזעה בפלג הגוף העליון, תחושת חום, אי־שקט, סיוטים.  חולשת דם : עייפות עם קושי להירדם, חלומות מרובים, עייפות כללית. חולשת yin  : יקיצות מרובות, תחושת חום בערב, חום בכפות הידיים והרגליים, בחזה או בפנים, צמא ויובש בפה בלילה.  חולשת טחול: נדודי שינה מלווים בתחושות דאגה ומחשבות חוזרות שלא מרפות, השמנה,     נחירות, sleep apnea, כבדות ועייפות במהלך היום. למה לפנות לטיפול סיני כשיש הפרעות בשינה?שינה טובה אינה נולדת ממאמץ, אלא מיכולת להרפות. כשמערכות הגוף פועלות בהרמוניה, השינה היא תוצאה טבעית.ברפואה הסינית אנחנו עובדים עם הגוף והנפש יחד, כדי להחזיר את המעגל הטבעי של יום ולילה. ההסתכלות היא הוליסטית: נטפל בסימפטום (הפרעות השינה) ובמקביל נטפל גםב"שורש" שלו. זהו טיפול עמוק ובסיסי, שתוצאותיו ארוכות טווח, ושמביא לא רק לשיפור באיכות השינה, אלאגם לבריאות כללית טובה יותר.', '/assets/images/an mian.webp', '2025-10-14 16:19:55');
